import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, Button, CardMenu, Iconbutton } from 'react-mdl';

class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = {activeTab: 0 };
    }
    toggleCategories(){
        if(this.state.activeTab === 0){
            return(
                <Card shadow={5} sytle={{minWidth: '450', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '176px', background: 'url(url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>
                        React Project #1
                    </CardTitle>
                </Card>
            )
        }
        else if (this.state.activeTab === 1){
            return(
                <div>
                    <h1>This is iOS</h1>
                </div>
            )
        }
        else if (this.state.activeTab === 2){
            return(
                <div>
                    <h1>This is Embedded</h1>
                </div>
            )
        }
    }



    render() {
        return(
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTab: tabId})} ripple>
                    <Tab>React</Tab>
                    <Tab>IOS</Tab>
                    <Tab>Embedded</Tab>
                </Tabs>
                <section className="projects-grid">
                    <Grid className="projects">
                        <Cell col={12}>
                            <div className="content">{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>
                </section>
            </div>
        )
    }
}

export default Projects;
